package com.assessment.ekos.base;import lombok.Data;import lombok.NoArgsConstructor;import lombok.experimental.SuperBuilder;import org.hibernate.annotations.Where;import org.springframework.core.style.ToStringCreator;import org.springframework.data.jpa.domain.support.AuditingEntityListener;import javax.persistence.*;import java.io.Serializable;import java.util.Objects;@MappedSuperclass@NoArgsConstructor@Data@SuperBuilder@EntityListeners(AuditingEntityListener.class)@Where(clause = "active = true")public class BaseEntity implements Serializable {    @Id    @GeneratedValue(strategy = GenerationType.SEQUENCE)    @Column(name = "base_id")    public Long id;    @Override    public int hashCode() {        return Objects.hash(id);    }    @Override    public boolean equals(final Object obj) {        if (this == obj) {            return true;        }        return obj != null && getClass() == obj.getClass() && Objects.equals(id, ((BaseEntity) obj).id);    }    @Override    public String toString() {        return toString(new ToStringCreator(this)).toString();    }    protected ToStringCreator toString(ToStringCreator creator) {        return creator.append("id", getId());    }}