package com.assessment.ekos.controller;import com.assessment.ekos.dto.LoginDto;import com.assessment.ekos.dto.UserDto;import com.assessment.ekos.service.UserService;import lombok.RequiredArgsConstructor;import org.springframework.http.ResponseEntity;import org.springframework.web.bind.annotation.PostMapping;import org.springframework.web.bind.annotation.RequestBody;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.RestController;import javax.validation.Valid;@RestController@RequiredArgsConstructor@RequestMapping("/auth")public class AuthController {    private final UserService userService;    @PostMapping("/login")    public ResponseEntity<?> authenticateUser(@Valid @RequestBody LoginDto loginDto) {        return this.userService.authenticateUser(loginDto);    }    @PostMapping("/sign-up")    public ResponseEntity<?> registerUser(@Valid @RequestBody UserDto userDto) {        return this.userService.createEkosUser(userDto);    }}