package com.assessment.ekos.controller;import com.assessment.ekos.dto.UserDto;import lombok.RequiredArgsConstructor;import org.springframework.web.bind.annotation.PostMapping;import org.springframework.web.bind.annotation.RequestBody;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.RestController;@RestController@RequiredArgsConstructor@RequestMapping("/user")public class UserController {    @PostMapping    public UserResource save(@RequestBody UserDto dto) {        dto.setPassword(encoder.encode(dto.getPassword()));        return super.save(dto);    }}