package com.assessment.ekos.model;import com.assessment.ekos.base.BaseEntity;import lombok.AllArgsConstructor;import lombok.Data;import lombok.EqualsAndHashCode;import lombok.NoArgsConstructor;import javax.persistence.*;import java.util.ArrayList;import java.util.List;@NoArgsConstructor@AllArgsConstructor@EqualsAndHashCode(callSuper = true)@Data@Entity@Table(        name = "ekos_user",        uniqueConstraints = {                @UniqueConstraint(                        name = "email",                        columnNames = {"email"}),        })public class User extends BaseEntity {    @Column(name = "email")    private String email;    @Column(name = "password")    private String password;    @Column(name = "firstname")    private String firstName;    @Column(name = "lastname")    private String lastName;    @ManyToMany    @JoinTable(            name = "user_roles",            joinColumns = {@JoinColumn(name = "user_id", referencedColumnName = "base_id")},            inverseJoinColumns = {@JoinColumn(name = "role_id", referencedColumnName = "base_id")})    private List<Role> roleList = new ArrayList<>();}