package com.assessment.ekos.service;import com.assessment.ekos.dto.UserDto;import com.assessment.ekos.model.User;import lombok.RequiredArgsConstructor;import org.springframework.http.ResponseEntity;import org.springframework.security.authentication.AuthenticationManager;import org.springframework.security.authentication.UsernamePasswordAuthenticationToken;import org.springframework.security.core.Authentication;import org.springframework.security.core.context.SecurityContextHolder;import org.springframework.security.crypto.password.PasswordEncoder;import org.springframework.stereotype.Service;import java.util.Optional;@RequiredArgsConstructor@Servicepublic class UserService {    private final AuthenticationManager authenticationManager;    private final PasswordEncoder encoder;    private final UserMapper mapper;    private final UserRepository userRepository;    private final UserMapper userMapper;    public User save(UserDto userDto) {        User user = userMapper.toEntity(userDto);        checkUser(user);        user.setPassword(encoder.encode(user.getPassword()));        return super.save(user);    }    public ResponseEntity<?> authenticateUser(LoginDto loginDto) {        Optional<User> user = userRepository.findOneByEmail(loginDto.getEmail());        if (user.isPresent() && encoder.matches(loginDto.getPassword(), user.get().getPassword())) {            Authentication authentication =                    authenticationManager.authenticate(                            new UsernamePasswordAuthenticationToken(loginDto.getEmail(), loginDto.getPassword()));            SecurityContextHolder.getContext().setAuthentication(authentication);            String jwt = this.authorizationService.getJWTToken(user.get());            UserDto dto = mapper.toDto(user.get());            TokenDto tokenDto = new TokenDto();            tokenDto.setToken(jwt);            dto.setTokenDto(tokenDto);            return ResponseEntity.ok(dto);        } else {            throw new BadRequestException(MessageUtil.USER_NOT_FOUND.getKey());        }    }}